#define GUIENTITY_EDITOR "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<interface>\n  <!-- interface-requires gtk+ 2.12 -->\n  <!-- interface-naming-policy toplevel-contextual -->\n  <object class=\"GtkWindow\" id=\"window1\">\n    <child>\n      <object class=\"GtkVBox\" id=\"vbox1\">\n        <child>\n          <object class=\"GtkEventBox\" id=\"alchera-title\">\n            <child>\n              <object class=\"GtkLabel\" id=\"alchera-label\">\n                <property name=\"xalign\">0</property>\n                <property name=\"xpad\">4</property>\n                <property name=\"ypad\">4</property>\n                <property name=\"label\" translatable=\"yes\">&lt;big&gt;&lt;b&gt;Entity Editor&lt;/b&gt;&lt;/big&gt;</property>\n                <property name=\"use_markup\">True</property>\n              </object>\n            </child>\n          </object>\n          <packing>\n            <property name=\"expand\">False</property>\n            <property name=\"position\">0</property>\n          </packing>\n        </child>\n        <child>\n          <object class=\"GtkScrolledWindow\" id=\"scrolledwindow1\">\n            <property name=\"can_focus\">True</property>\n            <property name=\"hscrollbar_policy\">automatic</property>\n            <property name=\"vscrollbar_policy\">automatic</property>\n            <child>\n              <placeholder/>\n            </child>\n          </object>\n          <packing>\n            <property name=\"position\">1</property>\n          </packing>\n        </child>\n        <child>\n          <object class=\"GtkHButtonBox\" id=\"hbuttonbox1\">\n            <child>\n              <object class=\"GtkButton\" id=\"button-save\">\n                <property name=\"label\">gtk-save</property>\n                <property name=\"can_focus\">True</property>\n                <property name=\"receives_default\">True</property>\n                <property name=\"use_stock\">True</property>\n              </object>\n           